import React, { Component } from "react";
import axios from "axios";
class About extends Component {
  constructor(props) {
    super(props);
    this.state = {
        massive: []
    };
  }



  componentDidMount() {
    axios.all([this.getText()]).then(axios.spread(function(massive, perms) {}));
  }
  getText() {
    return axios.get(`http://localhost:1337/blockeds/${3}`).then(response => {
      this.setState({ massive: response.data });
    });
  }
  render() {
    const textAbout = this.state.massive.map((input, i) => (
      <div key={i}>{input.Title}</div>
    ));

    return (
      <div className="section_center">
        <div>{textAbout}</div>
      </div>
    );
  }
}
export default About;
