import React, { Component } from 'react';
import { createSorter } from './createsort';
import axios from 'axios';
import { createFilter } from './filter';
class List extends Component {
  state = {
    filters: this.props.filters,
    sorters: this.props.sorters,
  };

  static defaultProps = {
    filters: [
      {
        property: 'TypeTitle',
        value: '',
      },
      {
        property: 'proizvoditel',
        value: '',
      },
    ],

    sorters: [
      {
        property: 'TypeTitle',
      },
      {
        property: 'proizvoditel',
      },
    ],
  };

  componentDidMount() {
    axios
      .get('http://localhost:1337/Items')
      .then(response => {
        // Handle success.
        this.setState({ data: this.parseData(response.data) });
      })
      .catch(error => {
        // Handle error.
        console.log('An error occurred:', error);
      });
  }

  parseData(data) {
    const { sorters } = this.state;

    if (data && data.length) {
      if (Array.isArray(sorters) && sorters.length) {
        data.sort(createSorter(...sorters));
      }
    }

    return data;
  }

  render() {
    const { data } = this.state;

    return data ? this.renderData(data) : this.renderLoading();
  }

  renderData(data) {
    if (data && data.length > 0) {
      const { filters } = this.state;

      if (Array.isArray(filters) && filters.length) {
        data = data.filter(createFilter(...filters));
      }

      return (
        <div className="item_block m-2 p-3" key={i}>
        <img src={'../img/' + data.image.name} className="img-fluid mt-2 mb-2" alt="Картинка" />
        <p>{data.NameItem}</p>
        <div className="col-md-12">
          <span>Цена</span>
          <span>{data.price}</span>
          грн
        </div>
      </div>
      );
    } else {
      return <div>No items found</div>;
    }
  }

  renderLoading() {
    return <div>Loading...</div>;
  }
}

export default List;
