import React, { Component } from 'react';
import axios from 'axios';
class Catalog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      massive: [],
    };
    // Request API.
  }
  componentDidMount() {
    axios
      .get(`http://localhost:1337/Items/`)
      .then(response => {
        // Handle success.
        this.setState({ massive: response.data });
      })
      .catch(error => {
        // Handle error.
        console.log('An error occurred:', error);
      });
  }
  render() {
    const itemProduct = this.state.massive.map((massive, i) => (
      <div className="item_block col-md-4 ml-2 mr-2 p-3" key={i}>
        <img src={'../img/' + massive.image.name} className="img-fluid mt-2 mb-2" alt="Картинка" />
        <p>{massive.NameItem}</p>
        <div className="col-md-12">
          <span>Цена</span>
          <span>{massive.price}</span>
          грн
        </div>
      </div>
    ));
    return (
      <div className="section_center catalog">
        <div className="container d-flex flex-row">
          <div className="col-md-2 ml-1 mr-1">
            <div className="col-md-12 mt-2 text_catalog">Фильтр</div>
          </div>
          <div className="col-md-10 ml-1 mr-1">
            <div className="col-md-12 mt-2 text_catalog">Каталог</div>
            <div className="col-md-12 container_items d-flex">{itemProduct}</div>
          </div>
        </div>
      </div>
    );
  }
}
export default Catalog;
